package com.example.month_3_lesson_3;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import java.util.ArrayList;

public class FragmentBikesShop extends Fragment {
    private ArrayList<Gen_cl> bikes = new ArrayList<Gen_cl>();
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_bikes_shop, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        initRV();
    }

    private void loadData() {
        bikes.add(new Gen_cl("kawaski ninja","Sport bike","data:image/jpeg;base64," ));
        bikes.add(new Gen_cl("Honda evo6","Street","https://i.pinimg.com/736x/e3/88/85/e38885974ff7f7f5581cd0907d72c8dc.jpg"));
        bikes.add(new Gen_cl("Ducati panigal","Sport bike","https://i.pinimg.com/originals/4d/81/7b/4d817b69f02a123c68f5bd36cbc991ee.jpg"));
        bikes.add(new Gen_cl("KTM SX-E 3","Enduro","https://avatars.mds.yandex.net/i?id=ae6cfa4568888a3f2077d1332054eb7739bc3e9c-8219252-images-thumbs&n=13"));
        bikes.add(new Gen_cl("BMW S1000R","Sport","https://uncrate.com/p/2020/12/bmw-s1000r-1.jpg"));
        bikes.add(new Gen_cl("BMW R1250gs","Adventure","https://ultimatemotorcycling.com/wp-content/uploads/2021/07/2022-bmw-r-1250-gs-adventure-first-look-adv-motorcycle-19.jpg"));
    }

    private void initRV() {
        RecyclerView recyclerView = requireActivity().findViewById(R.id.rv_bikes);
        BikesAdapter adapter = new BikesAdapter(this.getLayoutInflater(),bikes);
        recyclerView.setAdapter(adapter);

    }
}